import axios from "axios";
import Book from "../pages/book/Book";

const BookApis ={};
var access_token = localStorage.getItem('auth_token');
BookApis.index = async()=>{
const url = 'http://127.0.0.1:8000/api/book';
    const res = await axios.get(url,{
        headers: {
            'Authorization': `Bearer ${access_token}`
          }
    }).then(response=>{
        return response;
    }).catch(error=>{
         return error.response
    })

    return res;
}
BookApis.show = async(id)=>{
const url = `http://127.0.0.1:8000/api/book/${id}`;
    const res = await axios.get(url).then(response=>{
        return response;
    }).catch(error=>{
         return error.response
    })

    return res;
}

export default BookApis
