import { wait } from '@testing-library/user-event/dist/utils';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import BookApis from '../../apis/BookApis';

 const BookEdit = () => {
    const {id} =useParams();
    const [book, setBook]= useState({
      book_name: '',
      auth_name: '',
      publish_date: '',
      book_name: '',
    });
    

    useEffect(()=>{
        showBookDataById(id);
    },[id])

     const showBookDataById = async(id)=>{
        const res = await BookApis.show(id);
        if(res.data.status ==200){
            // console.log('first', res);
            setBook(res.data.data);
        }
     }

  return (
    <div className='container mt-5'>
        <div className='card'>
           <div className='card-header'>
             <h2>Edit Book</h2>
           </div>
           <div className='card-body'>
           <form>
            <div className='row'>
             <div className='col-6'>
                <div className="form-outline mb-4">
                   <input type="text" name='book_name' placeholder='Book Name' className="form-control" />
                </div>
             </div>
             <div className='col-6'>
                <div className="form-outline mb-4">
                   <input type="text" name='auth_name' placeholder='Auther Name'  className="form-control" />
   
                 </div>
             </div>
             <div className='col-6'>
             <div className="form-outline mb-4">
                   <input type="date" name='publish_date' placeholder='Publish Date'   className="form-control" />
                </div>
             </div>
             <div className='col-6'>
             <div className="form-outline mb-4">
                   <input type="number" name='edition' placeholder='Edition' id="form4Example1" className="form-control" />

                </div>
             </div>
            </div>
       
       <div className="form-check d-flex justify-content-center mb-4">
    </div>
     <button type="submit" className="btn btn-primary btn-block mb-4">Update</button>
       </form>
           </div>
        </div>
        
    </div>
  )
}
export default BookEdit
