const BearerToken= {};
var access_token = localStorage.getItem('auth_token');

BearerToken.token =  {
    headers: {
        'Authorization': `Bearer ${access_token}`
      }
}

showAlert.s = (title,text ) => {
    swal.fire({
        title: title,
        text: text,
        icon: "success",
        confirmButtonText: "OK",
      });
  }

export default BearerToken;
