import React, { useState } from 'react'
import { Link } from 'react-router-dom';

const Login = () => {

const [loginInput, setLoginInput] =useState({
    email: '',
    password: '',
});

const handleInputChange =(e)=>{
    e.persist();
    setLoginInput({...loginInput, [e.target.name]: e.target.value})

}

const handleSubmit=(e)=>{
      e.preventDefault();
}

  return (
    <section className="vh-100 gradient-custom">
  <div className="container py-5 h-100">
    <div className="row d-flex justify-content-center align-items-center h-100">
      <div className="col-12 col-md-8 col-lg-6 col-xl-5">
        <div className="card bg-dark text-white">
          <div className="card-body p-5 text-center">
            <div className="mb-md-5 mt-md-4 pb-5">
              <h2 className="fw-bold mb-2 text-uppercase">Login</h2>
              <p className="text-white-50 mb-5">Please enter your login and password!</p>
              <form onSubmit={handleSubmit}>
                 <div className="form-outline form-white mb-4">
                     <input type="email" onChange={handleInputChange} value={loginInput.name} placeholder='Email...' id="typeEmailX" className="form-control form-control-lg" />
                </div>
                <div className="form-outline form-white mb-4">
                    <input type="password"  onChange={handleInputChange} value={loginInput.email} placeholder='Password...' id="typePasswordX" className="form-control form-control-lg" />
                </div>
                 <button className="btn btn-outline-light btn-lg px-5" type="submit">Login</button>
              </form>
 
            </div>
            <div>
              <p className="mb-0">Don't have an account? <Link to="/registration" className="text-white-50 fw-bold">Sign Up</Link>
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
  )
}
export default Login;
