import {
    wait
} from '@testing-library/user-event/dist/utils';
import React, {
    useEffect,
    useState
} from 'react'
import {
    useParams
} from 'react-router-dom'
import BookApis from '../../apis/BookApis';

const BookEdit = () => {
    const {
        id
    } = useParams();
    const [book, setBook] = useState();
    // console.log('book data',book);

    useEffect(() => {
        showBookDataById(id);
    }, [id])

    const showBookDataById = async (id) => {
        const res = await BookApis.show(id);
        if (res.data.status == 200) {
            // console.log('first', res);
            setBook(res.data.data);
        }
    }

    return ( <
        div className = 'container mt-5' >
        <
        div className = 'card' >
        <
        div className = 'card-header' >
        <
        h2 > Edit Book < /h2> <
        /div> <
        div className = 'card-body' >
        <
        form >
        <
        div className = "form-outline mb-4" >
        <
        input type = "text"
        id = "form4Example1"
        className = "form-control" / >
        <
        label className = "form-label"
        for = "form4Example1" > Name < /label> <
        /div> <
        div className = "form-outline mb-4" >
        <
        input type = "email"
        id = "form4Example2"
        className = "form-control" / >
        <
        label className = "form-label"
        for = "form4Example2" > Email address < /label> <
        /div> <
        div className = "form-outline mb-4" >
        <
        textarea className = "form-control"
        id = "form4Example3"
        rows = "4" > < /textarea> <
        label className = "form-label"
        for = "form4Example3" > Message < /label> <
        /div> <
        div className = "form-check d-flex justify-content-center mb-4" >
        <
        /div> <
        button type = "submit"
        className = "btn btn-primary btn-block mb-4" > Send < /button> <
        /form> <
        /div> <
        /div>

        <
        /div>
    )
}
export default BookEdit
